--- re index object that have the "index" property otherwise doesnt work at all
--- @return table containing the table without the new element and the removed element
function Utils:removeAndReindex(table,index)



  local lostVal table.remove(table,index)
    if not Table.is_empty(table) and size(table) > 1 then
        for i, v in pairs(table) do
            if i >= index then
                if(not Is.Nil(v.index))then
                    v.index = v.index - 1
                end
            end
        end
    end

    local result = {}
    result.remove = lostVal
    result.table = table
    return result
end